import axios from'axios';// Prefer environment variable, fallback to window origin (for dev),
// and finally a sensible default for local development.
const runtimeBase=process.env.REACT_APP_API_BASE_URL&&"".concat(process.env.REACT_APP_API_BASE_URL)||(typeof window!=='undefined'?window.location.origin:'')||'http://localhost:8000';const API_BASE_URL="".concat(runtimeBase.replace(/\/$/,''),"/api");const api=axios.create({baseURL:API_BASE_URL,timeout:30000,headers:{'Content-Type':'application/json'}});export class NewsAPI{static async getNews(){const response=await api.get('/news');return response.data;}static async getSources(){const response=await api.get('/sources');return response.data;}static async getSourceStatus(sourceName){const encodedSourceName=encodeURIComponent(sourceName);const response=await api.get("/sources/".concat(encodedSourceName,"/status"));return response.data;}static async refreshNews(){const response=await api.post('/refresh');return response.data;}}export default NewsAPI;
