import React from 'react';
import { NewsHeadline } from '../services/api';

interface HeadlineItemProps {
  headline: NewsHeadline;
  className?: string;
}

const HeadlineItem: React.FC<HeadlineItemProps> = ({ 
  headline, 
  className = '' 
}) => {
  const formatDate = (dateString: string): string => {
    try {
      const date = new Date(dateString);
      const now = new Date();
      const diffMs = now.getTime() - date.getTime();
      const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
      const diffMins = Math.floor(diffMs / (1000 * 60));

      if (diffMins < 60) {
        return `${diffMins}m ago`;
      } else if (diffHours < 24) {
        return `${diffHours}h ago`;
      } else {
        return date.toLocaleDateString();
      }
    } catch (error) {
      return 'Unknown';
    }
  };

  return (
    <div className={`border-b border-gray-200 last:border-b-0 py-3 ${className}`}>
      <a 
        href={headline.link}
        target="_blank"
        rel="noopener noreferrer"
        className="block hover:bg-gray-50 transition-colors duration-150 rounded px-2 py-1 -mx-2 -my-1"
      >
        <h3 className="text-sm font-medium text-gray-900 mb-1 leading-5">
          {headline.title}
        </h3>
        <div className="flex items-center text-xs text-gray-500 space-x-2">
          <span>{headline.source}</span>
          <span>â€¢</span>
          <time dateTime={headline.published_at}>
            {formatDate(headline.published_at)}
          </time>
        </div>
      </a>
    </div>
  );
};

export default HeadlineItem;
